SELECT form.*, `section`.`id` as `section.id`, `section`.`form_id` as `section.form_id`, `section`.`title` as `section.title`, `section`.`description` as `section.description`, `section`.`sequence` as `section.sequence`, `section`.`is_active` as `section.is_active`, `question`.`id` as `question.id`, `question`.`section_id` as `question.section_id`, `question`.`title` as `question.title`, `question`.`subtitle` as `question.subtitle`, `question`.`question_type_id` as `question.question_type_id`, `question`.`is_active` as `question.is_active`, `question`.`mandatory` as `question.mandatory`, `question`.`sequence` as `question.sequence`, `question_option`.`id` as `question_option.id`, `question_option`.`question_id` as `question_option.question_id`, `question_option`.`description` as `question_option.description`, `question_option`.`sequence` as `question_option.sequence`, `question_option`.`total_value` as `question_option.total_value`, `question_option`.`net_promoter_from` as `question_option.net_promoter_from`, `question_option`.`net_promoter_to` as `question_option.net_promoter_to`, `question_option`.`is_active` as `question_option.is_active` FROM form, section, client, question LEFT JOIN question_option ON (question.id = question_option.question_id AND question_option.is_active = 1) WHERE section.form_id = form.id AND question.section_id = section.id And form.client = client.id
AND client.token = #{clientToken}
AND form.super_form_id = #{superFormId,jdbcType=VARCHAR}
AND form.is_active = 1 AND section.is_active = 1 AND question.is_active = 1 And client.is_active = 1 ORDER BY form.id, question.section_id, question.sequence, question_option.sequence

